mapper.select:
SELECT *
FROM review
WHERE tool_id = #{toolId}

    AND status = #{status}

ORDER BY helpful_count DESC, created_at DESC

SELECT *
FROM review
WHERE tool_id = #{toolId}
AND status = 1
ORDER BY helpful_count DESC, rating DESC
LIMIT #{limit}

SELECT *
FROM review
WHERE tool_id = #{toolId}
AND user_id = #{userId}
LIMIT 1

mapper.update:
UPDATE review
SET helpful_count = helpful_count + 1
WHERE id = #{id}
