mapper.select:
SELECT *
FROM review
WHERE tool_id = #{toolId}

    AND status = #{status}

ORDER BY helpful_count DESC, created_at DESC

SELECT *
FROM review
WHERE tool_id = #{toolId}
AND user_id = #{userId}
LIMIT 1

mapper.update:
UPDATE review
SET helpful_count = helpful_count + 1
WHERE id = #{id}

mapper.select:
SELECT *
FROM review
WHERE tool_id = #{toolId}
AND parent_id IS NULL

    AND status = #{status}

ORDER BY helpful_count DESC, created_at DESC

SELECT *
FROM review
WHERE parent_id = #{parentId}

    AND status = #{status}

ORDER BY created_at ASC

SELECT *
FROM review
WHERE parent_id = #{parentId}

    AND status = #{status}

ORDER BY created_at ASC

mapper.update:
UPDATE review
SET reply_count = reply_count + 1
WHERE id = #{id}
