mapper.select:
SELECT t.*
FROM tool t
WHERE t.status = 1

    AND (
        t.name LIKE CONCAT('%', #{keyword}, '%')
        OR t.tagline LIKE CONCAT('%', #{keyword}, '%')
        OR t.description LIKE CONCAT('%', #{keyword}, '%')
    )


    AND t.category_id = #{categoryId}


    AND t.pricing_model = #{pricingModel}


    AND t.average_rating >= #{minRating}



        ORDER BY t.view_count ${sortOrder}


        ORDER BY t.upvote_count ${sortOrder}


        ORDER BY t.average_rating ${sortOrder}, t.review_count DESC


        ORDER BY t.launch_date ${sortOrder}


        ORDER BY t.upvote_count DESC

SELECT *
FROM tool
WHERE status = 1
AND id != #{toolId}
AND category_id = #{categoryId}
ORDER BY upvote_count DESC, average_rating DESC
LIMIT #{limit}

mapper.update:
UPDATE tool
SET view_count = view_count + 1
WHERE id = #{id}

UPDATE tool
SET favorite_count = favorite_count + 1
WHERE id = #{id}

UPDATE tool
SET upvote_count = upvote_count + 1
WHERE id = #{id}
